import React, { useState, useEffect } from 'react';

function Testimonial_Element({id_media, title, content}){

    const [url_media, setUrlMedia] =  useState([]);

    useEffect(() => {
        const fetchURLMedia = async () => {
          try {

            if(id_media == 0){    
                id_media = 7;
            }

            const endpoint = 'http://localhost:8888/wp-json/wp/v2/media/' + id_media;

            // Realiza una solicitud GET a la API de WordPress
            const response = await fetch(endpoint);

            // Verifica si la solicitud fue exitosa
            if (response.ok) {
              // Extrae los datos JSON de la respuesta
              const data = await response.json();

              // Actualiza el estado de los posts con los datos obtenidos
              setUrlMedia(data);
            } else {
              // Si la solicitud no fue exitosa, muestra un mensaje de error
              console.error('Error al obtener los posts:', response.statusText);
            }
          } catch (error) {
            // Si hay algún error durante la solicitud, muestra el mensaje de error
            console.error('Error al obtener los posts:', error);
          }
        };

        // Llama a la función para obtener los posts cuando el componente esté montado
        fetchURLMedia();
      }, []);



    content = content.replace('<p>', '');
    content = content.replace('</p>', '');

    return (
      <div>
        <img src={url_media.source_url} className='testimonial-img'/>
        <h2>
             {title}
        </h2>
        <p>
            {content}
        </p>
      </div>
    );
  }

  export {Testimonial_Element};
